import React, {useState} from 'react';
import './ExpenseForm.css';


const ExpenseForm = (props) => {
    const [title, setTitle] = useState('');
    const [amount, setAmount] = useState('');
    const [date, setDate] = useState('');

    // const [userInput, setUserInput] = useState({title: '', amount: '', date: ''});

    const titleChangeHandler = (event) => {
        setTitle(event.target.value);

        // setUserInput({
        //     ...userInput,
        //     title: event.target.value,
        // });

        // setUserInput((prevState) => {
        //     return {...prevState, title: event.target.value};
        // });
    };

    const amountChangeHandler = event => {
        setAmount(event.target.value);

        // setUserInput({
        //     ...userInput,
        //     amount: event.target.value,
        // });
    };

    const dateChangeHandler = event =>{
        setDate(event.target.value);

        // setUserInput({
        //     ...userInput,
        //     date: event.target.value,
        // });
    };

    // const inputChangeHandler = (identifier, value) => {
    //     if(identifier === 'title'){
    //         setTitle(value);
    //     }else if (identifier === 'amount') {
    //         setAmount(value);
    //     }else {
    //         setDate(value);
    //     }
    // };

    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: title,
            amount: amount,
            date: new Date(date)
        }
        // console.log(expenseData);
        props.onSaveExpenseData(expenseData);
        setTitle('');
        setAmount('');
        setDate('');
    }

    const closeFormHandler = () => {

    }


    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={title} onChange={titleChangeHandler}/>
                    {/* <input type="text" onChange={(event) => inputChangeHandler('title', event.target.value)}/> */}
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" value={amount} min="0.01" step="0.01" onChange={amountChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" value={date} min="2021-01-01" max="2023-10-11" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="cancel" onClick={closeFormHandler}>Cancel</button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
